import { Component } from '@angular/core';
import { FormGroup, Validators, FormBuilder } from '@angular/forms';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { ThreadService } from './threads.service';

@Component({
  selector: 'app-threads-threadform',
  templateUrl: './threadform.component.html', // path to the HTML component
  styleUrls: ['../../css/thread_view.css'],
})

export class ThreadformComponent {
  threadForm: FormGroup; // Initialise a FormGroup object

  constructor(
    private _formBuilder: FormBuilder,
    private _router: Router, // Initialise router object for navigation
    private _threadService: ThreadService,
    private _http: HttpClient)
  {
    this.threadForm = _formBuilder.group({
      // Define FormBuilder input validation rules
      createdBy: ['', Validators.required],
      threadTitle: ['', [Validators.required, Validators.maxLength(100)]],
      threadBody: ['', [Validators.required, Validators.maxLength(2500)]],

    });
  }


  onSubmit() {
    console.log("ThreadCreate from submitted:");
    console.log(this.threadForm);
    console.log('The thread ' + this.threadForm.value.title + ' is created.');
    console.log(this.threadForm.touched);
    const newThread = this.threadForm.value; // Creates a Thread object with values from the form
    this._threadService.createThread(newThread) //Send the new thread to ThreadService->ThreadController to save
      .subscribe(response => {
      if (response.success) {
        console.log(response.message);
        this._router.navigate(['/threads']);  // navigates back to /threads
      }
      else {
        console.log('Thread creation failed');
      }
    });
  }

  backToThreads() {
    this._router.navigate(['/threads']); //navigates back to table view of threads
  }
}
<h2 class="tabelLabel text-white">Create Thread</h2>

<div class="form-box">
  <div class="create-container">
    <form [formGroup]="threadForm" (ngSubmit)="onSubmit()">
      <div class="form-group">
        <div class="create-information">
          <label class="input-label" for="createdBy">Username:</label>
            <input type="text" id="createdBy" formControlName="createdBy" class="form-control username-input" />
        </div>

        <div class="create-information">
          <label class="input-label" for="threadTitle">Title:</label>
            <input type="text" id="threadTitle" formControlName="threadTitle" class="form-control title-input" />
            <div class="error-form" *ngIf="threadForm.get('threadTitle')?.hasError('maxlength')">
              <b>Maximum length exceeded (max 100 characters).</b>
            </div>
        </div>

        <div class="create-information">
          <label class="input-label" for="threadBody">Body:</label>
            <textarea type="text" id="threadBody" formControlName="threadBody" class="form-control body-input"></textarea>
            <div class="error-form" *ngIf="threadForm.get('threadBody')?.hasError('maxlength')">
              <b>Maximum length exceeded (max 2500 characters).</b>
            </div>
        </div>
      </div>

      <div class="create-thread-buttons">
        <button type="submit" class="btn btn-primary create-button" [disabled]="threadForm.invalid">Create Thread</button>
        <button class="btn btn-secondary cancel-button" (click)="backToThreads()">Cancel</button>
      </div>

    </form>
  </div>
</div>
