import { Component, OnInit, OnDestroy } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ThreadService } from './threads.service';
import { Thread } from './threads';
import { Comment } from '../comments/comments';
import { takeUntil } from 'rxjs/operators';
import { Subject } from 'rxjs';
import {CommentsService} from "../comments/comments.service";

@Component({
  selector: 'app-thread-view',
  templateUrl: './thread-view.component.html',
  styleUrls: ['../../css/thread_view.css']
})
export class ThreadViewComponent implements OnInit, OnDestroy {
  thread: Thread = {} as Thread;
  newCommentBody: string = '';
  newCommentCreatedBy: string = '';
  isEditing = false;
  editedTitle!: string;
  editedContent!: string;

  private unsubscribe$ = new Subject<void>();

  constructor(private route: ActivatedRoute, private threadService: ThreadService) {}
  // fetches the thread and the comments under the thread
  ngOnInit(): void {
    this.route.paramMap.pipe(
      takeUntil(this.unsubscribe$)
    ).subscribe(params => {
      const threadId = +params.get('id')!;
      // fetches the thread by threadId
      this.threadService.getThread(threadId).subscribe(
        (thread: Thread) => {
          this.thread = thread;
          // fetches the comment that belongs to the thread by threadId
          this.threadService.getCommentsForThread(threadId).subscribe(
            (comments: Comment[]) => {
              this.thread.threadComments = comments;
            },
            (error) => {
              console.error('Error fetching comments', error);
            }
          );
        },
        (error) => {
          console.error('Error fetching thread', error);
        }
      );
    });
  }
  // makes a constructor that takes in several data for comment. It is used in the HTML "thread-view.component.html" to add the data from the input
  addComment() {
    const newComment = {
      commentId: 0,
      commentBody: this.newCommentBody,
      commentCreatedAt: new Date(),
      commentLastEditedAt:new Date(),
      threadId: this.thread.threadId,
      thread: null,
      parentCommentId: null,
      parentComment: null,
      createdBy: this.newCommentCreatedBy,
      childComments: [],
    };
  // adds the new comments to the thread it belongs to by the threadId
    this.threadService.addCommentToThread(this.thread.threadId, newComment).subscribe(
      (updatedThread: Thread) => {
        this.thread = updatedThread;
        this.newCommentBody = '';
        this.newCommentCreatedBy = '';
      },
      (error) => {
        console.error('Error adding comment', error);
      }
    );
  }

  // deletes the thread by threadId
  deletePost(thread: Thread){
    const confirmDelete = confirm(`Are you sure you want to delete "${thread.threadTitle}"`);
    if (confirmDelete){
      this.threadService.deleteThread(thread.threadId).subscribe(
        (response) => {
          if (response.success){
            console.log(response.message);
          }
        },
        error => {
          console.error('Error deleting item', error)
        }
      )
    }
  }

  toggleEdit() {
    this.isEditing = !this.isEditing;
  }

  saveChanges(): void {
    // Perform the logic to save changes, update thread.title and thread.body
    // Call the service method to update the thread
    this.thread.threadTitle = this.editedTitle;
    this.thread.threadBody = this.editedContent;

    this.threadService.updateThread(this.thread).subscribe(
      (response) => {
        console.log(response.message);
        // Reset isEditing flag after successful save
        this.isEditing = false;
      },
      (error) => {
        console.error('Error saving changes', error);
      }
    );
  }

  cancelEdit(): void {
    // Reset editedTitle and editedContent with the current values
    this.editedTitle = this.thread.threadTitle;
    this.editedContent = this.thread.threadBody;
    // Reset isEditing flag
    this.isEditing = false;
  }

  ngOnDestroy(): void {
    this.unsubscribe$.next();
    this.unsubscribe$.complete();
  }

  protected readonly CommentsService = CommentsService;
}
<div class="form-box">
  <div class="create-container">
    <div class="form-group">
      <div *ngIf="thread.threadId; else notFound">
        <div *ngIf="!isEditing; else editMode" id="displayPost">
          <!-- Display content when not editing -->
          <h1 class="thread-title-current">{{ thread.threadTitle }}</h1>

          <div class="thread-information">
            <!-- Thread information: user, post date, last edited date -->
            <p>
              Posted by <b>{{ thread.createdBy }}</b> on the <b>{{ thread.threadCreatedAt | date: 'yyyy-MM-dd HH:mm' }}</b>.
              <!-- Display last edited date if different -->
              <span *ngIf="thread.threadCreatedAt !== thread.threadLastEditedAt">
                <i>Last edited: {{ thread.threadLastEditedAt | date: 'yyyy-MM-dd HH:mm' }}</i>
              </span>
            </p>
            <hr />
            <p class="thread-body">{{ thread.threadBody }}</p>
          </div>
          <div><p class="comment-count"><img src="../../assets/images/message-detail-regular-24.png" alt="..." height="20" />
            {{ thread.threadComments?.length }}</p></div>
          <button (click)="toggleEdit()" class="editBtn">Edit</button>
          <button (click)="deletePost(thread)" [routerLink]="['/threads']" class="editBtn">Delete</button>
        </div>

        <ng-template #editMode>
          <!-- Display editable content when editing -->
          <input type="text" [(ngModel)]="editedTitle" class="editTitle"/>
          <div class="thread-information">
            <!-- Thread information: user, post date, last edited date -->
            <p>
              Posted by <b>{{ thread.createdBy }}</b> on the <b>{{ thread.threadCreatedAt | date: 'yyyy-MM-dd HH:mm' }}</b>.
              <!-- Display last edited date if different -->
              <span *ngIf="thread.threadCreatedAt !== thread.threadLastEditedAt">
                <i>Last edited: {{ thread.threadLastEditedAt | date: 'yyyy-MM-dd HH:mm' }}</i>
              </span>
            </p>
            <hr />
            <textarea [(ngModel)]="editedContent" class="editBox"></textarea>
          </div>
          <div><p class="comment-count"><img src="../../assets/images/message-detail-regular-24.png" alt="..." height="20" />
            {{ thread.threadComments?.length }}</p></div>
          <button (click)="saveChanges()" [routerLink]="['/thread-view/', thread.threadId]" class="editBtn">Save</button>
          <button (click)="cancelEdit()" class="editBtn">Cancel</button>
        </ng-template>
      </div>

      <ng-template #notFound>
        <div class="container">
          <p>The requested thread was not found.</p>
        </div>
      </ng-template>

      <div id="displayComments" *ngIf="thread">
        <h3 class="comments-header">Comments</h3>

        <!-- Input box for adding comments -->
        <div id="textBox">
          <input [(ngModel)]="newCommentCreatedBy" placeholder="Display name" class="comment-input">
          <textarea [(ngModel)]="newCommentBody" placeholder="What are your thoughts?" class="comment-input"></textarea>
          <div>
            <button (click)="addComment()" class="add-comment-button">Add Comment</button>
          </div>
        </div>

        <!-- Display existing comments -->
        <div class="actual-comment">
          <div *ngIf="!thread.threadComments || thread.threadComments.length === 0">
            <p>No comments yet.</p>
          </div>
          <div *ngFor="let comment of thread.threadComments">
            <p> <b>{{comment.createdBy}}</b> replied on the <b>{{comment.commentCreatedAt | date:'yyyy-MM-dd HH:mm'}}</b>
              <!-- Displays commentLastEdited value only if it differs from commentCreatedAt        -->
              <ng-container *ngIf="comment.commentLastEditedAt !== comment.commentCreatedAt">
              <i>. Last edited at: {{ comment.commentLastEditedAt | date:'yyyy-MM-dd HH:mm' }}</i>
              </ng-container>
            </p>
            <p>{{ comment.commentBody }}</p>
            <div>
              <button  class="commentBtn" >Edit</button>
              <button class="commentBtn">Delete</button>

            </div>
            <hr>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
