import { Component, OnInit, OnDestroy } from '@angular/core';
import { FormGroup, Validators, FormBuilder } from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { ThreadService } from './threads.service';
import { Thread } from './threads';
import { Comment } from '../comments/comments';
import { takeUntil } from 'rxjs/operators';
import { Subject } from 'rxjs';

@Component({
  selector: 'app-thread-view',
  templateUrl: './thread-view.component.html',
  styleUrls: ['../../css/thread_view.css']
})
export class ThreadViewComponent implements OnInit, OnDestroy {
  thread: Thread = {} as Thread;
  threadView: FormGroup;
  newCommentBody: string = '';
  newCommentCreatedBy: string = '';
  isEditing = false;
  editedTitle: string = '';
  editedBody: string = '';

  private unsubscribe$ = new Subject<void>();

  constructor(
    private _formBuilder: FormBuilder,
    private router: ActivatedRoute,
    private threadService: ThreadService
  )
  {
    this.threadView = _formBuilder.group({
      newCommentCreatedBy: ['', [Validators.required, Validators.maxLength(50)]],
      newCommentBody: ['', [Validators.required, Validators.maxLength(1000)]],

    });
  }

  // fetches the thread and the comments under the thread
  ngOnInit(): void {
    this.router.paramMap.pipe(
      takeUntil(this.unsubscribe$)
    ).subscribe(params => {
      const threadId = +params.get('id')!;
      // fetches the thread by threadId
      this.threadService.getThread(threadId).subscribe(
        (thread: Thread) => {
          this.thread = thread;
          // fetches the comment that belongs to the thread by threadId
          this.threadService.getCommentsForThread(threadId).subscribe(
            (comments: Comment[]) => {
              this.thread.threadComments = comments;
            },
            (error) => {
              console.error('Error fetching comments', error);
            }
          );
        },
        (error) => {
          console.error('Error fetching thread', error);
        }
      );
    });
  }
  // makes a constructor that takes in several data for comment. It is used in the HTML "thread-view.component.html" to add the data from the input
  addComment() {
    const newComment = {
      commentId: 0,
      commentBody: this.newCommentBody,
      commentCreatedAt: new Date(),
      commentLastEditedAt:new Date(),
      threadId: this.thread.threadId,
      thread: null,
      parentCommentId: null,
      parentComment: null,
      createdBy: this.newCommentCreatedBy,
      childComments: [],
    };
    this.threadService.addCommentToThread(this.thread.threadId, newComment).subscribe(
      (response) => {
        this.newCommentBody = '';
        this.newCommentCreatedBy = '';
        newComment.commentId = response.commentId;
        this.thread.threadComments!.push(newComment); //Adds new comment to local representation of thread (because the thread is already loaded and would otherwise need to be refreshed)


      },
      (error) => {
        console.error('Error adding comment', error);
      }
    );
  }

  // deletes the thread by threadId
  deletePost(thread: Thread){
    const confirmDelete = confirm(`Are you sure you want to delete "${thread.threadTitle}"`);
    if (confirmDelete){
      this.threadService.deleteThread(thread.threadId).subscribe(
        (response) => {
          if (response.success){
            this.router.navigate(['/']); //Navigate to home page after deleting a thread.
            console.log(response.message);
          }
        },
        error => {
          console.error('Error deleting item', error)
        }
      )
    }
  }

  toggleEdit(thread: Thread) {
    this.isEditing = !this.isEditing;
    this.editedBody = thread.threadBody;
    this.editedTitle = thread.threadTitle;

  }

  // saves the changes made on the thread.
  saveChanges(): void {
    // Perform the logic to save changes, update thread.title and thread.body
    // Call the service method to update the thread
    this.thread.threadTitle = this.editedTitle;
    this.thread.threadBody = this.editedBody;

    this.threadService.updateThread(this.thread).subscribe(
      (response) => {
        console.log('Server response:', response);

        // Update the thread with the response from the server
        if (response.success) {
          this.thread.threadLastEditedAt = response.updatedThread.threadLastEditedAt;

          this.toggleEdit(this.thread);
        } else {
          console.error('Error updating thread. Server response:', response);
        }
      },
      (error) => {
        console.error('Error saving changes', error);
      }
    );
  }


  deleteComment(commentId: number): void {
    this.threadService.deleteComment(commentId).subscribe(
      () => {
        console.log('Comment deleted');

        // This ensure that the UI update gets updated after deletion
        if (this.thread && this.thread.threadComments) {
          this.thread.threadComments = this.thread.threadComments.filter(comment => comment.commentId !== commentId);
        }
      },
      (error) => console.error('Error deleting comment', error)
    );
  }

  toggleEditComment(comment: Comment): void {
    comment.isEditing = !comment.isEditing;
    this.editedBody = comment.commentBody;
  }

  saveChangesComment(comment: Comment, editedBody: string): void {
    if (editedBody !== undefined && editedBody.length >= 1) {
      comment.commentBody = editedBody;
      this.threadService.updateComment(comment).subscribe(
        (response) => {
          console.log("Comment has been updated");
          this.toggleEditComment(comment);
        },
        (error) => {
          console.error('Error saving changes', error);
        }
      );
    } else {
      console.error('Attempted to save changes with undefined editedBody');
    }
  }


  // LastEditedAt value is only displayed when time difference is over 1s (60*1000ms)
  significantTimeDifference(object: any): boolean {
    let timeDiff: number;
    if ("threadCreatedAt" in object) { //If object contains threadCreatedAt field, it must be a thread.
       timeDiff = new Date(object.threadLastEditedAt).getTime() - new Date(object.threadCreatedAt).getTime();
    } else {
       timeDiff = new Date(object.commentLastEditedAt).getTime() - new Date(object.commentCreatedAt).getTime();
    }
    return timeDiff > (60*1000)

  }

  ngOnDestroy(): void {
    this.unsubscribe$.next();
    this.unsubscribe$.complete();
  }
}
<div class="form-box">
  <div class="create-container">
    <div class="form-group">

      <div *ngIf="thread.threadId; else notFound">
        <div *ngIf="!isEditing; else editMode" id="displayPost">
          <!-- Display content when not editing -->
          <h1 class="thread-title-current">{{ thread.threadTitle }}</h1>

          <div class="thread-information">
            <!-- Thread information: user, post date, last edited date -->
            <p>
              Posted by <b>{{ thread.createdBy }}</b> on the <b>{{ thread.threadCreatedAt | date: 'yyyy-MM-dd HH:mm' }}</b>.
              <!-- Display last edited date if different -->
              <span *ngIf="significantTimeDifference(thread)">
                <i>Last edited: {{ thread.threadLastEditedAt | date: 'yyyy-MM-dd HH:mm' }}</i>
              </span>
            </p>
            <hr />
            <p class="thread-body">{{ thread.threadBody }}</p>
          </div>

          <div><p class="comment-count"><img ngSrc="../../assets/images/message-detail-regular-24.png" alt="..." height="20" width="20"/>
            {{ thread.threadComments?.length }}</p>
          </div>

          <button (click)="toggleEdit(thread)" class="editBtn">Edit</button>
          <button (click)="deletePost(thread)" [routerLink]="['/threads']" class="editBtn">Delete</button>
        </div>

        <ng-template #editMode>
          <!-- Display editable content when editing -->
          <input type="text" [(ngModel)]="editedTitle" class="editTitle"/>
          <div class="thread-information">
            <!-- Thread information: user, post date, last edited date -->
            <p>
              Posted by <b>{{ thread.createdBy }}</b> on the <b>{{ thread.threadCreatedAt | date: 'yyyy-MM-dd HH:mm' }}</b>.
              <!-- Display last edited date if different -->
              <span *ngIf="(significantTimeDifference(thread))">
                <i>Last edited: {{ thread.threadLastEditedAt | date: 'yyyy-MM-dd HH:mm' }}</i>
              </span>
            </p>
            <hr />
            <textarea [(ngModel)]="editedBody" class="editBox"></textarea>
          </div>

          <div><p class="comment-count"><img ngSrc="../../assets/images/message-detail-regular-24.png" alt="..." height="20" width="20" />
            {{ thread.threadComments?.length }}</p>
          </div>

          <button (click)="saveChanges()" class="editBtn">Save</button>
          <button (click)="toggleEdit(thread)" class="editBtn">Cancel</button>
        </ng-template>
      </div>

      <ng-template #notFound>
        <div class="container">
          <p>The requested thread was not found.</p>
        </div>
      </ng-template>

      <div id="displayComments" *ngIf="thread">
        <h3 class="comments-header">Comments</h3>

        <!-- Input box for adding comments -->
        <div id="textBox">
          <input [(ngModel)]="newCommentCreatedBy" placeholder="Display name" class="comment-input">
          <div class="error-form" *ngIf="threadView.get('newCommentCreatedBy')?.hasError('maxlength')">
            <b>Maximum length exceeded (max 50 characters).</b>
          </div>
          <textarea [(ngModel)]="newCommentBody" placeholder="What are your thoughts?" class="comment-input"></textarea>
          <div class="error-form" *ngIf="threadView.get('newCommentBody')?.hasError('maxlength')">
            <b>Maximum length exceeded (max 1000 characters).</b>
          </div>
          <div>
            <button (click)="addComment()" class="add-comment-button" [disabled]="threadView.invalid">Add Comment</button>
          </div>
        </div>

        <!-- Display existing comments -->
        <div class="actual-comment">
          <div *ngIf="!thread.threadComments || thread.threadComments.length === 0">
            <p>No comments yet.</p>
          </div>

          <div *ngFor="let comment of thread.threadComments">
            <div *ngIf="!comment.isEditing; else editMode">
              <!-- Display content when not editing -->
              <p>
                <b>{{comment.createdBy}}</b> replied on the <b>{{comment.commentCreatedAt | date:'yyyy-MM-dd HH:mm'}}</b>
                <!-- Displays commentLastEdited value only if it differs from commentCreatedAt        -->
                <ng-container *ngIf="(significantTimeDifference(comment))">
                  <i>. Last edited at: {{ comment.commentLastEditedAt | date:'yyyy-MM-dd HH:mm' }}</i>
                </ng-container>
              </p>
              <p>{{ comment.commentBody }}</p>

              <div>
                <button class="commentBtn" (click)="toggleEditComment(comment)">Edit</button>
                <button class="commentBtn" (click)="deleteComment(comment.commentId)">Delete</button>
              </div>
              <hr>
            </div>

            <ng-template #editMode>
              <!-- Display editable content when editing -->
              <p>
                <b>{{comment.createdBy}}</b> replied on the <b>{{comment.commentCreatedAt | date:'yyyy-MM-dd HH:mm'}}</b>
                <!-- Displays commentLastEdited value only if it differs from commentCreatedAt        -->
                <ng-container *ngIf="comment.commentLastEditedAt !== comment.commentCreatedAt">
                  <i>. Last edited at: {{ comment.commentLastEditedAt | date:'yyyy-MM-dd HH:mm' }}</i>
                </ng-container>
              </p>
              <textarea class="comment-input" type="text" [(ngModel)]="comment.editedBody"></textarea>
              <div>
                <button class="commentBtn" (click)="saveChangesComment(comment, editedBody)">Save</button>
                <button class="commentBtn" (click)="toggleEditComment(comment)">Cancel</button>
              </div>
              <hr>
            </ng-template>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
