import { Component } from '@angular/core';

@Component({
  selector: 'app-nav-menu',
  templateUrl: './nav-menu.component.html',
  styleUrls: ['./nav-menu.component.css']
})
export class NavMenuComponent {
  isExpanded = false; //sets the value to false tells us if the navmenu is expanded or not

  collapse() {
    this.isExpanded = false; // isExpanded is set to false if the method "collapse" is called
  }

  toggle() {
    this.isExpanded = !this.isExpanded; // the boolean is changed from  true to false if the method "toggle" is called and vice versa
  }
}

<script>
  document.addEventListener("DOMContentLoaded", function () {
    const searchBar = document.getElementById("searchBar");
    const searchResultsDropdown = document.getElementById('searchResultsDropdown');

    searchResultsDropdown.style.display = 'none';

    searchBar.addEventListener("input", function () {
      handleSearchInput();
    });
  });
</script>

<header>
  <nav
    class="navbar navbar-expand-sm navbar-toggleable-sm navbar-light bg-white border-bottom box-shadow mb-3"
  >
    <div class="container">
      <a class="navbar-brand" [routerLink]="['/']">ArcaNet</a>
      <button
        class="navbar-toggler"
        type="button"
        data-toggle="collapse"
        data-target=".navbar-collapse"
        aria-label="Toggle navigation"
        [attr.aria-expanded]="isExpanded"
        (click)="toggle()"
      >
        <span class="navbar-toggler-icon"></span>
      </button>
      <div
        class="navbar-collapse collapse d-sm-inline-flex justify-content-end"
        [ngClass]="{ show: isExpanded }"
      >
        <div class="position-relative">
          <input id="searchBar" class="form-control" placeholder="Search" type="text">
          <div id="searchResultsDropdown" class="search-results"></div>
        </div>
        <ul class="navbar-nav flex-grow">
          <li
            class="nav-item"
            [routerLinkActive]="['link-active']"
            [routerLinkActiveOptions]="{ exact: true }"
          >
          </li>
          <li class="nav-item" [routerLinkActive]="['link-active']">
            <a class="nav-link text-dark" [routerLink]="['/threads']"> Explore </a>
          </li>
        </ul>
      </div>
    </div>
  </nav>
</header>
